import React from 'react';
import bikcraft from './img/bikcraft.jpeg'
import animaisFantasticos from './img/animaisfantasticos.jpeg';
import styled from 'styled-components';

const Titulo = styled.h1`
    margin-bottom: 60px;
    text-align: center;
`;

const Slider = styled.div`
    margin: 0 auto;
    width: 800px;
    height: 400px;
    overflow: hidden;
`;

const Slides = styled.div`
    width: 400%;
    height: 400px;
    display: flex
`;

const Input = styled.input`
    display: none;
`;

const Slide = styled.div`
    width: 25%;
    position: relative
`;

const Img = styled.img`
    width: 800px;
`;

const NavegacaoBtn1 = styled.div`

`;

const NavegacaoBtn2 = styled.div`

`;

const NavegacaoAuto = styled.div`

`;

const ManualNavegacao = styled.div`
    position: absolute;
    width: 800px;
    margin-top: -40px;
    display: flex;
    justify-content: center
`;

const ManualBtn = styled.label`
    border: 2px solid #FFF;
    padding: 5px;
    border-radius: 10px;
    cursor: pointer;
    transition: 1s;
    &:not(:last-child) {
        margin-right: 10px;
    }
    &:hover {
        background-color: #FFF;
    }
`;


const Projetos = () => {
  return (
    <>
        <Titulo>Projetos</Titulo>

        <Slider>
            <Slides>
                <Input type="radio" id='radio1'></Input>
                <Input type="radio" id='radio2'></Input>

                <Slide>
                   <Img src={bikcraft}></Img>
                </Slide>

                <Slide>
                    <Img src={animaisFantasticos}></Img>
                </Slide>

                <NavegacaoAuto>
                    <NavegacaoBtn1></NavegacaoBtn1>
                    <NavegacaoBtn2></NavegacaoBtn2>
                </NavegacaoAuto>

            </Slides>

            <ManualNavegacao>
                <ManualBtn htmlFor="radio1"></ManualBtn>
                <ManualBtn htmlFor="radio2"></ManualBtn>
            </ManualNavegacao>
        </Slider>
    </>
  )
};

export default Projetos;
