import React from 'react';
import bikcraft from './img/bikcraft.jpeg'
import animaisFantasticos from './img/animaisfantasticos.jpeg';
import styled from 'styled-components';


const Slider = styled.div`
    margin: 0 auto;
    width: 800px;
    height: 400px;
    overflow: hidden
`;

const slides = {
    width: '400%',
    height: '400px',
    display: 'flex'
};

const input = {
    display: 'none'
};

const slide1 = {

};

const slide = {
    width: '25%',
    position: 'relative'
};

const navegacao = {

};

const navegacaoBtn1 = {

};

const navegacaoBtn2 = {

};

const manualNavegacao = {
    position: 'absolute',
    width: '800px',
    marginTop: '-40px',
    display: 'flex',
    justifyContent: 'center'
};


const ManualBtn = styled.label`
    border: 2px solid #FFF;
    padding: 5px;
    border-radius: 10px;
    cursor: pointer;
    transition: 1s;
    margin-right: 10px;
`;


const Projetos = () => {
  return (
    <>
        <h1 style={{marginTop: '60px', marginBottom: '60px', textAlign:'center'}}>Projetos</h1>
        <div style={slider}>

            <div style={slides}>
                <input style={input} type="radio" id="radio1"/>
                <input style={input} type="radio" id="radio2"/>

                <div style={slide}>
                    <img src={bikcraft} alt="bikcraft" style={{width: '800px'}}/>
                </div>
                <div style={slide}>
                    <img src={animaisFantasticos} style={{width: '800px'}} alt="bikcraft"/>
                </div>

                <div style={navegacao}>
                    <div stlye={navegacaoBtn1}></div>
                    <div stlye={navegacaoBtn2}></div>
                </div>

            </div>

            <div style={manualNavegacao}>
                <ManualBtn htmlFor="radio1"></ManualBtn>
            </div>

        </div>
    </>
  )
};

export default Projetos;
