import React from 'react';
import bikcraft from './img/bikcraft.jpeg'
import animaisFantasticos from './img/animaisfantasticos.jpeg';
import styled from 'styled-components';


const Slider = styled.div`
    margin: 0 auto;
    width: 800px;
    height: 400px;
    overflow: hidden;
`;

const Slides = styled.div`
    width: 400%;
    height: 400px;
    display: flex
`;

const Input = styled.input`
    display: none;
`;

const Slide = styled.div`
    width: 25%;
    position: relative
`;

const Img = styled.img`
    width: 800px;
`;

const navegacaoBtn1 = {

};

const navegacaoBtn2 = {

};

const manualNavegacao = {
    position: 'absolute',
    width: '800px',
    marginTop: '-40px',
    display: 'flex',
    justifyContent: 'center'
};


const ManualBtn = styled.label`
    border: 2px solid #FFF;
    padding: 5px;
    border-radius: 10px;
    cursor: pointer;
    transition: 1s;
    margin-right: 10px;
`;


const Projetos = () => {
  return (
    <>
        <h1 style={{marginTop: '60px', marginBottom: '60px', textAlign:'center'}}>Projetos</h1>

        <Slider>
            <Slides>
                <Input type="radio" id='radio1'></Input>
                <Input type="radio" id='radio2'></Input>

                <Slide>
                   <Img src={bikcraft}></Img>
                </Slide>

                <Slide>
                    <Img src={animaisFantasticos}></Img>
                </Slide>

                <div>
                    <div stlye={navegacaoBtn1}></div>
                    <div stlye={navegacaoBtn2}></div>
                </div>

            </Slides>

            <div style={manualNavegacao}>
                <ManualBtn htmlFor="radio1"></ManualBtn>
                <ManualBtn htmlFor="radio2"></ManualBtn>
            </div>
        </Slider>
    </>
  )
};

export default Projetos;
